import { config } from '../env-config';
export const info = (message) => {
    console.info(message);
};
function logRequest(env, level, id, data) {
    const log = JSON.stringify({
        level,
        env,
        pageUrl: window.location.origin,
        message: { id, data }
    });
    if (env === 'local') {
        console[level](log);
    }
    else {
        fetch(config(env).LOGGING_ENDPOINT, {
            method: 'POST',
            body: log,
            mode: 'no-cors'
        });
    }
}
export const infoLogRequest = (id, data, env) => {
    logRequest(env, 'info', id, data);
};
export const debugLogRequest = (id, data, env) => {
    logRequest(env, 'debug', id, data);
};
export const errorLogRequest = (id, data, env) => {
    logRequest(env, 'error', id, data);
};
