import gsap from 'gsap';
export class MenuDrawerAnimation {
    constructor() {
        this.slideIn = (element) => {
            element.querySelector('.menu-drawer-level-1').setAttribute('style', 'visibility: visible;');
            gsap.to(element.querySelector('.drawer-level-1'), {
                right: '0%',
                duration: 0.32,
                ease: 'sine.inOut'
            });
        };
        this.slideOut = (element) => {
            gsap.to(element.querySelector('.drawer-level-1'), {
                right: '-100%',
                duration: 0.24,
                ease: 'sine.inOut',
                onComplete: () => {
                    element.querySelector('.menu-drawer-level-1').setAttribute('style', 'visibility:hidden');
                }
            });
        };
    }
}
