'use strict';

const breakpointCustomizable = require('./breakpointCustomizable-2c3f2e06.js');

const calcLineHeightForElement = (tag) => {
    const { fontSize } = window.getComputedStyle(tag);
    // fontSize is "" when element does no longer exist and would throw an exception in calculateLineHeight
    return fontSize && breakpointCustomizable.calculateLineHeight(fontSize);
};

exports.calcLineHeightForElement = calcLineHeightForElement;
