'use strict';

const index_esm = require('./index.esm-21a5c361.js');

/**
 * Listens to the end of a CSS transition and calls a throttled callback and calls
 * the callback once initially.
 * @param tag
 * @param transitionProperty
 * @param callback
 * @returns void
 */
const transitionListener = (tag, transitionProperty, callback) => {
    window.requestAnimationFrame(() => {
        tag.addEventListener('transitionend', index_esm.throttle(50, e => {
            if (e.propertyName === transitionProperty) {
                callback();
            }
        }));
        callback();
    });
};

exports.transitionListener = transitionListener;
