export class Locale {
    constructor(locale) {
        this.setLocale(locale);
    }
    setLocale(locale) {
        this.language = locale.split('-')[0];
        this.country = locale.split('-')[1];
    }
    toString() {
        return `${this.language}-${this.country}`;
    }
    toHeaderString() {
        return `${this.language}_${this.country}`;
    }
}
