import { createStore } from '@stencil/store';
import { SLOT_UPDATED_EVENT } from './constants';
export const navStateStore = createStore({
    activeDrawerContent: null,
    parentDrawerContent: [],
    accessibilityMarker: null,
    backButtonText: null
});
navStateStore.onChange('activeDrawerContent', (_) => {
    const slotUpdatedEvent = new CustomEvent(SLOT_UPDATED_EVENT);
    const headerElement = document.querySelector('phn-header');
    if (headerElement) {
        const menuDrawer = headerElement.shadowRoot.querySelector('.drawer-trigger');
        menuDrawer.dispatchEvent(slotUpdatedEvent);
    }
});
